RULES_ITEM.SQL_TEXT:
declare
    v_new_file_name         varchar2(4000);
    v_message               varchar2(400);
    v_domain                varchar2(400);

    v_start_date            date;
    v_finish_date           date;

    v_send_report           number;
    v_blob_data_id          number;
    v_sum_err_integrations  number := 0;
    v_sum_err_reports       number := 0;
    v_sum_err_rules         number := 0;
    v_sum_err_imports       number := 0;
    v_sum_err_notifications number := 0;
    v_sum_err_trackor_mail  number := 0;

begin

    begin
        select process_id
          into v_send_report
          from process p
          join param_program_value ppv on (
               ppv.value = p.user_id
               and ppv.program_id = pkg_sec.get_pid()
               and p.process_id = :process_id
          )
          join param_program pp on (
               pp.param_program_id = ppv.param_program_id
               and pp.name = 'AsyncRuleDefaultUser'
          );
    exception when no_data_found then
        return;
    end;

    select value
      into v_domain
      from param_system
     where name='DomainName';

    v_new_file_name := 'Weekly App Health - ' || v_domain;

    for cur in (
        select t_param.value_date "VALUE_DATE",
               report_blob_data_id "BLOB_ID"
          from report_run t_run
          left join report_run_param t_param on (
               t_param.report_run_id = t_run.report_run_id
          )
         where t_run.process_id = :process_id
         order by t_param.value_date asc
    ) loop

        if cur.VALUE_DATE is not null then
            if v_start_date is null then
                v_start_date := cur.VALUE_DATE;
            elsif v_finish_date is null then
                v_finish_date := cur.VALUE_DATE;
            end if;
        end if;

        v_blob_data_id := cur.BLOB_ID;

    end loop;

    if v_start_date is null then
        v_start_date := current_date - 7;
    end if;

    if v_finish_date is null then
        v_finish_date := current_date;
    end if;

    for cur_sum_errors in (
        select p.process_type_id "PROCESS_ID", 
               count(*) "SUM_ERROR"
          from process p
          join process_status ps on (p.status_id = ps.process_status_id)
          left join integration_run ir on (ir.process_id = p.process_id)
          left join integration i on (i.integration_id = ir.integration_id)
          left join rule_run rr on (rr.process_id = p.process_id)
          left join rule r on (r.rule_id = rr.rule_id)
          left join report_run rfr on (rfr.process_id = p.process_id)
          left join report_files rf on (rf.report_file_id = rfr.report_file_id)
          left join imp_run ir on (ir.process_id = p.process_id)
          left join imp_spec isp on (ir.imp_spec_id = isp.imp_spec_id)
         where p.submission_date between v_start_date and v_finish_date
           and ((p.process_type_id in (1 /*Report*/, 2 /*Import*/, 5 /*Integration*/, 6 /*Rule*/)
               and p.status_id in (3 /*Executed with Errors*/, 14 /*Interrupted*/, 20 /*Canceled: Num Cells Limit Exceeded*/))
               or (p.process_type_id in (2 /*Import*/, 5 /*Integration*/, 6 /*Rule*/)
                  and p.status_id in (0 /*Pending*/, 4 /*In Queue*/) and p.start_date < current_date)
           )
         group by p.process_type_id
    ) loop

        if cur_sum_errors.PROCESS_ID = 1 /*Report*/ then
            v_sum_err_reports      := v_sum_err_reports + cur_sum_errors.SUM_ERROR;
        elsif cur_sum_errors.PROCESS_ID = 2 /*Import*/ then
            v_sum_err_imports      := v_sum_err_imports + cur_sum_errors.SUM_ERROR;
        elsif cur_sum_errors.PROCESS_ID = 5 /*Integration*/ then
            v_sum_err_integrations := v_sum_err_integrations + cur_sum_errors.SUM_ERROR;
        elsif cur_sum_errors.PROCESS_ID = 6 /*Rule*/ then
            v_sum_err_rules        := v_sum_err_rules + cur_sum_errors.SUM_ERROR;
        end if;

    end loop;

    select count(distinct t_nq.notif_queue_id)
      into v_sum_err_notifications
      from notif_queue t_nq
      join notif_queue_attempt t_nqa on (
           t_nqa.notif_queue_id = t_nq.notif_queue_id
      )
     where t_nq.created_ts between v_start_date and v_finish_date
       and t_nq.queue_status_id = 4 /*Failed. No more attempts*/;

    select count(t_log.tm_log_id)
      into v_sum_err_trackor_mail
      from tm_log t_log
      join tm_log_status t_status on (
           t_status.tm_log_status_id = t_log.status_id
      )
      join tm_setup t_setup on (
           t_setup.tm_setup_id = t_log.tm_setup_id
      )
      join xitor_type t_xitor_type on (
           t_xitor_type.xitor_type_id = t_setup.xitor_type_id
      )
     where t_log.ts between v_start_date and v_finish_date
       and t_log.status_id in (1 /*Processing error*/, 3 /*Processing error (TF)*/, 4 /*Job error*/);

    update blob_data
       set filename = v_new_file_name || '.xlsx'
     where blob_data_id = v_blob_data_id;

    v_message := 'Import Errors - ' || v_sum_err_imports || '; Integrations Errors - ' || v_sum_err_integrations || '; Report Errors - ' || v_sum_err_reports || '; Rules Errors - ' || v_sum_err_rules || '; Notification Errors - ' || v_sum_err_notifications || '; Trackor Mail Errors - ' || v_sum_err_trackor_mail || ';';

    util.sendmailwithattachments(
        v_new_file_name, 
        v_message, 
        '', 
        'noc-reports@onevizion.com', 
        null, 
        'html', 
        null, 
        v_blob_data_id
    );

end;

declare
    c_report_name constant varchar2(500) := 'Weekly App Health Report';

    v_report_id   number;
    v_report      number;

begin

    begin
        select report_file_id
          into v_report_id
          from report_files
         where report_name = c_report_name;
    exception when no_data_found then
        util.raiseerror('The report ' || c_report_name || ' is not found');
    end;

    v_report := util.schedulereport(
        PREPORTID => v_report_id,
        PUSERID => util.getcurrentuserid(),
        PPARAMETERS => 'Start Date[Default Value] = End Date[Default Value] =',
        PFILEDELIVERY => 1
    );

end;
